let slideIndex = 1;
let totalSlides = document.getElementsByClassName('slide').length;

function currentSlide(n) {
    slideIndex = n;
    showSlides(slideIndex);
}

function showSlides(n) {
    const slides = document.querySelector('.slides');

    if (n > totalSlides) {
        slideIndex = 1;
    }
    if (n < 1) {
        slideIndex = totalSlides;
    }

    // Calculate the new transform value for sliding effect
    const offset = -(slideIndex - 1) * 100;
    slides.style.transform = `translateX(${offset}%)`;

    // Update dot active state
    const dots = document.getElementsByClassName("dot");
    for (let i = 0; i < dots.length; i++) {
        dots[i].className = dots[i].className.replace(" active", "");
    }
    dots[slideIndex - 1].className += " active";
}

// Initialize the first slide
showSlides(slideIndex);

// Optional: Add auto-sliding functionality
setInterval(function() {
    slideIndex++;
    showSlides(slideIndex);
}, 10000); // Auto-slide every 5 seconds

